            <tr>
                <td>{{ option.id }}</td>
                <td>{{ option.nomOption }}</td>
                <td>
                    <a href=\"{{ path('options_show', {'id': option.id}) }}\">
                        <button class=\"btn btn-outline-dark\">Voir</button>
                    </a>
                    <a href=\"{{ path('options_edit', {'id': option.id}) }}\">
                        <button class=\"btn btn-outline-dark\">Edition</button>
                    </a>
                </td>
            </tr>
